const configCommand = getVariable("configCommands");
const showCommands = getVariable("showCommands");
const username = getVariable("username");
const jobReportJsonData = getVariable("rawShowOutput");

// Replace placeholders with actual values
const configCommandOutput = configCommand.replace(/{{(.*?)}}/g, (_, varName) => getVariable(varName));

let devicesToConfigure = {};
let allDevices = {};
let reportAvailable = true;
let resultList = {};
let errorList = { errors: [] };

function containsUser(array) {
  for (let i = 0; i < array.length; i++) {
    if (array[i].includes(`username ${username}`)) {
      return true;
    }
  }
  return false;
}

if (
  !jobReportJsonData ||
  !Array.isArray(jobReportJsonData)
) {
  reportAvailable = false;
  errorList.errors.push("No report available");
  console.error("Invalid JSON data format.");
} else {
  jobReportJsonData.forEach((dataItem) => {
    if (!dataItem) {
      reportAvailable = false;
      errorList.errors.push("No data available");
      return;
    }

    const deviceIP = dataItem.ip_address;
    const reachability_status = dataItem.device_status === "Reachable";
    let change_required = true;
    let current_config = "";
    let authentication = reachability_status;

    allDevices[deviceIP] = true;

    if (
      !dataItem.data_dump_show_commands ||
      !Array.isArray(dataItem.data_dump_show_commands) ||
      dataItem.data_dump_show_commands.length < 1
    ) {
      change_required = null;
      current_config = "NA";

      resultList[deviceIP] = {
        reachability_status,
        authentication,
        change_required,
        current_config,
      };

      reportAvailable = false;
      errorList.errors.push(`No data available for ${deviceIP}`);
      return;
    }

    let data_found = false;
    let jobOutputNotAvailable = false;

    dataItem.data_dump_show_commands.forEach((command) => {
      if (
        command &&
        command.json_output &&
        Array.isArray(command.json_output) &&
        command.command_name === `${showCommands}`
      ) {
        if (containsUser(command.json_output)) {
          change_required = false;
          data_found = true;
        }
        current_config = command.command_output;
      } else if (
        command &&
        command.error === "False" &&
        command.command_status === "Success" &&
        command.command_output === "No output received" &&
        command.command_name === `${showCommands}`
      ) {
        change_required = true;
        data_found = false;
        current_config = command.command_output;
      } else {
        jobOutputNotAvailable = true;
        change_required = null;
        current_config = "No output available";
        errorList.errors.push(`No output available for ${deviceIP}`);
      }
    });

    resultList[deviceIP] = {
      reachability_status,
      authentication,
      change_required,
      current_config,
    };

    if (!data_found && !jobOutputNotAvailable) {
      devicesToConfigure[deviceIP] = true;
      resultList[deviceIP]["config_command"] = configCommandOutput;
      resultList[deviceIP]["show_command"] = showCommands;
    } else {
      resultList[deviceIP]["config_command"] = "NA";
    }
  });
}

setVariable("expectedConfigData", resultList);

console.log("Devices to configure: ", devicesToConfigure);
console.log("All devices: ", allDevices);
console.log("Report available: ", reportAvailable);
console.log("Result list: ", resultList);
console.log("Error list: ", errorList);
console.log("Move Further: ", Object.keys(devicesToConfigure).length > 0);

return Object.keys(devicesToConfigure).length > 0;

/*
Available Context:
- handleError(error): Handle an error by displaying a snackbar message.
- setVariable(name, value): Set a variable with the given name and value.
- getVariable(name): Get the value of a variable with the given name.
- clearVariables(): Clear all variables.
- result: This variable contains the returned value from previous node.

Example:
  setVariable('exampleVar', 'exampleValue');
  const value = getVariable('exampleVar');
  if (value) {
    console.log(value)
  } else {
    handleError('Variable not found');
  }
*/

// Run your code here
const allNoOutput = getVariable("allNoOutput")

if (allNoOutput) {
  handleError("No Data Found")
} else {
  handleError("Configuration already exists")
}

const finalConfigOutput = getVariable("finalConfigOutput");
const showCommand = getVariable("show_commands");
const username = getVariable("username");

let devicesToConfigure = {};
let allDevices = {};
let reportAvailable = true;
let resultList = {};
let errorList = {};
errorList.errors = [];

function containsUser(array) {
  for (let i = 0; i < array.length; i++) {
    if (array[i].includes(`username ${username}`)) {
      return true;
    }
  }
  return false;
}

for (const [deviceIp, jobOutput] of Object.entries(finalConfigOutput)) {
  if (!jobOutput) {
    reportAvailable = false;
    errorList.errors.push(`No data available for ${deviceIp}`);
    continue;
  }

  const jobReportJsonData = jobOutput.job_report;

  if (!jobReportJsonData || !Array.isArray(jobReportJsonData)) {
    reportAvailable = false;
    errorList.errors.push(`No report available for ${deviceIp}`);
    continue;
  }

  jobReportJsonData.forEach((dataItem) => {
    if (!dataItem) {
      reportAvailable = false;
      errorList.errors.push(`No data available for ${deviceIp}`);
      return;
    }

    const reachabilityStatus = dataItem.device_status === "Reachable";
    let changeRequired = true;
    let currentConfig = "";
    let authentication = false;

    if (dataItem.device_status === "Reachable") {
      authentication = true;
    }

    allDevices[deviceIp] = true;

    if (
      !dataItem.data_dump_post ||
      !Array.isArray(dataItem.data_dump_post) ||
      dataItem.data_dump_post.length < 1
    ) {
      changeRequired = null;
      currentConfig = "NA";

      resultList[deviceIp] = {
        reachability_status: reachabilityStatus,
        authentication: authentication,
        change_required: changeRequired,
        current_config: currentConfig,
      };

      reportAvailable = false;
      errorList.errors.push(`No data available for ${deviceIp}`);
      return;
    }

    let dataFound = false;
    let jobOutputNotAvailable = false;

    dataItem.data_dump_post.forEach((command) => {
      if (
        command &&
        command.json_output &&
        Array.isArray(command.json_output) &&
        command.command_name === `${showCommand}`
      ) {
        const array = command.json_output;

        if (!containsUser(array)) {
          dataFound = true;
          changeRequired = false;
        }

        currentConfig = command.command_output;
      } else {
        changeRequired = null;
        jobOutputNotAvailable = true;
        currentConfig = "No output available";
        errorList.errors.push(`No output available for ${deviceIp}`);
      }
    });

    if (!dataFound && !jobOutputNotAvailable) {
      devicesToConfigure[deviceIp] = true;
    }

    resultList[deviceIp] = {
      reachability_status: reachabilityStatus,
      authentication: authentication,
      change_required: changeRequired,
      current_config: currentConfig,
    };
  });
}

function formatResultListPlainText(resultList) {
  let text = "Device Configuration Report\n\n";

  for (const [ip, data] of Object.entries(resultList)) {
    text += `Device IP: ${ip}
Reachable: ${data.reachability_status ? "Yes" : "No"}
Authenticated: ${data.authentication ? "Yes" : "No"}
Change Required: ${
      data.change_required === null
        ? "Unknown"
        : data.change_required
        ? "Yes"
        : "No"
    }
Current Config:
${data.current_config}
------------------------------------------------------------
`;
  }

  return text;
}

const plainTextOutput = formatResultListPlainText(resultList);

setVariable("plainTextOutput", plainTextOutput);
setVariable("viewpoint_output", JSON.stringify(resultList));
setVariable("devices_to_configure", JSON.stringify(devicesToConfigure));
setVariable("error_list", JSON.stringify(errorList));
setVariable("report_available", reportAvailable);

return Object.keys(devicesToConfigure).length !== Object.keys(allDevices).length;
