data.script:
const finalConfigOutput = getVariable("finalConfigOutput");
const banner_text = getVariable("banner_text");

var job_report_json_data = JSON.parse(job_report_response)

let devicesToConfigure = {};
let allDevices = {};
let reportAvailable = true;
let resultList = {};
let errorList = {};
errorList.errors = [];

for (const [deviceIP, jobOutput] of Object.entries(finalConfigOutput)) {
  if (!jobOutput) {
    reportAvailable = false;
    errorList.errors = ["No data available"];
    continue;
  }
  const showCommand = jobOutput.show_command;
  const jobReportJsonData = jobOutput.job_report;

  if (
    !jobReportJsonData ||
    !jobReportJsonData ||
    !Array.isArray(jobReportJsonData)
  ) {
    reportAvailable = false;
    errorList.errors = ["No report available"];
  } else {
    jobReportJsonData.forEach((dataItem) => {
      if (!dataItem) {
        reportAvailable = false;
        return;
      }

      const deviceIp = dataItem.ip_address;
      const reachabilityStatus = dataItem.device_status === "Reachable";
      let changeRequired = true;
      let currentConfig = "";
      let authentication = false;

      if (dataItem.device_status === "Reachable") {
        authentication = true;
      }

      allDevices[deviceIp] = true;

      if (
        !dataItem.data_dump_post ||
        !Array.isArray(dataItem.data_dump_post) ||
        dataItem.data_dump_post.length < 1
      ) {
        changeRequired = null;
        currentConfig = "NA";

        resultList[deviceIp] = {
          reachability_status: reachabilityStatus,
          authentication: authentication,
          change_required: changeRequired,
          current_config: currentConfig,
        };

        reportAvailable = false;
        errorList.errors = [`No data available for ${deviceIp}`];
        return;
      }

      let dataFound = false;
      let jobOutputNotAvailable = false;

      dataItem.data_dump_post.forEach((command) => {
        if (
          command &&
          command.json_output &&
          Array.isArray(command.json_output) &&
          command.command_name === `${showCommand}`
        ) {
          const array = command.json_output;

          for (let i = 0; i < array.length; i++) {
            if (array[i].includes(banner_text)) {
              dataFound = true;
              changeRequired = false;
              break;
            } else {
              changeRequired = true;
            }
          }

          currentConfig = command.command_output;
        } else {
          changeRequired = null;
          jobOutputNotAvailable = true;
          currentConfig = "No output available";
          errorList.errors.push(`No output available for ${deviceIp}`);
        }
      });

      if (!dataFound && !jobOutputNotAvailable) {
        devicesToConfigure[deviceIp] = true;
      }

      resultList[deviceIp] = {
        reachability_status: reachabilityStatus,
        authentication: authentication,
        change_required: changeRequired,
        current_config: currentConfig,
      };
    });
  }
}

const moveFurther = Object.keys(devicesToConfigure).length !== Object.keys(allDevices).length;

execution.setVariable("move_further", moveFurther)
execution.setVariable("viewpoint_output", S(JSON.stringify(resultList)))
execution.setVariable("devices_to_configure", S(JSON.stringify(devicesToConfigure)))
execution.setVariable("devices_for_backup", S(JSON.stringify(devicesForBackup)))
execution.setVariable("error_list", S(JSON.stringify(errorList)))
execution.setVariable("report_available", reportAvailable)

function formatResultListForEmail(resultList) {
  let html = `
    <h2>Device Configuration Report</h2>
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; font-family: Arial, sans-serif;">
      <thead>
        <tr style="background-color: #f2f2f2;">
          <th>Device IP</th>
          <th>Reachability</th>
          <th>Authenticated</th>
          <th>Change Required</th>
          <th>Current Config</th>
        </tr>
      </thead>
      <tbody>
  `;

  for (const [ip, data] of Object.entries(resultList)) {
    html += `
      <tr>
        <td>${ip}</td>
        <td>${data.reachability_status ? "Yes" : "No"}</td>
        <td>${data.authentication ? "Yes" : "No"}</td>
        <td>${data.change_required === null
        ? "Unknown"
        : data.change_required
          ? "Yes"
          : "No"
      }</td>
        <td><pre style="white-space: pre-wrap; max-width: 600px;">${data.current_config
      }</pre></td>
      </tr>
    `;
  }

  html += `
      </tbody>
    </table>
  `;

  return html;
}

function formatResultListPlainText(resultList) {
  let text = "Device Configuration Report\n\n";

  for (const [ip, data] of Object.entries(resultList)) {
    text += `Device IP: ${ip}
  Reachable: ${data.reachability_status ? "Yes" : "No"}
  Authenticated: ${data.authentication ? "Yes" : "No"}
  Change Required: ${data.change_required === null
        ? "Unknown"
        : data.change_required
          ? "Yes"
          : "No"
      }
  Current Config:
${data.current_config}
------------------------------------------------------------
`;
  }

  return text;
}

const itsmWorknotesBody = formatResultListPlainText(resultList);
const emailBody = formatResultListForEmail(resultList);

setVariable("itsmWorknotesOutput", itsmWorknotesBody);
setVariable("plainTextOutput", emailBody);

return (
  Object.keys(devicesToConfigure).length !== Object.keys(allDevices).length
);
