const configCommand = getVariable("configCommands");
const showCommands = getVariable("showCommands");
const service_name = getVariable("service_name");
const service_ip = getVariable("service_ip");
const jobReportJsonData = getVariable("rawShowOutput");

// Replace vars with actual values
const configCommandOutput = configCommand.replace(/{{(.*?)}}/g, (_, varName) => getVariable(varName));

console.log("Config Command: ", configCommandOutput);
console.log("Show Commands: ", showCommands);
console.log("Job Report JSON Data: ", jobReportJsonData);

let devicesToConfigure = {};
let allDevices = {};
let reportAvailable = true;
let resultList = {};
let errorList = {};
errorList.errors = [];

if (
  !jobReportJsonData ||
  !jobReportJsonData ||
  !Array.isArray(jobReportJsonData)
) {
  reportAvailable = false;
  errorList.errors = ["No report available"];
  console.error("Invalid JSON data format.");
} else {
  jobReportJsonData.forEach((dataItem) => {
    if (!dataItem) {
      reportAvailable = false;
      errorList.errors = ["No data available"];
      return;
    }

    const deviceIP = dataItem.ip_address;
    const reachability_status = dataItem.device_status === "Reachable";
    let change_required = true;
    let current_config = "";
    let authentication = false;

    if (dataItem.device_status === "Reachable") {
      authentication = true;
    }

    allDevices[deviceIP] = true;

    if (
      !dataItem.data_dump_show_commands ||
      !Array.isArray(dataItem.data_dump_show_commands) ||
      dataItem.data_dump_show_commands.length < 1
    ) {
      change_required = null;
      current_config = "NA";

      resultList[deviceIP] = {
        reachability_status,
        authentication,
        change_required,
        current_config,
      };

      reportAvailable = false;
      errorList.errors = [`No data available for ${deviceIP}`];
      return;
    }

    let data_found = false;
    let jobOutputNotAvailable = false;

    dataItem.data_dump_show_commands.forEach((command) => {
      if (
        command &&
        command.json_output &&
        Array.isArray(command.json_output) &&
        command.command_name === `${showCommands}`
      ) {
        const array = command.json_output;

        for (let i = 0; i < array.length; i++) {
          if (array[i].includes(service_name) && array[i].includes(service_ip)) {
            change_required = false;
            data_found = true;
            break;
          }
        }
        current_config = command.command_output;
      } else if (
        command &&
        command.error === "False" &&
        command.command_status === "Success" &&
        command.command_output === "No output received" &&
        command.command_name === `${showCommands}`
      ) {
        change_required = true;
        data_found = false;
        current_config = command.command_output;
      } else {
        jobOutputNotAvailable = true;
        change_required = null;
        current_config = "No output available";
        errorList.errors.push(`No output available for ${deviceIP}`);
      }
    });

    resultList[deviceIP] = {
      reachability_status,
      authentication,
      change_required,
      current_config,
    };

    if (!data_found && !jobOutputNotAvailable) {
      devicesToConfigure[deviceIP] = true;
      resultList[deviceIP]["config_command"] = configCommandOutput;
      resultList[deviceIP]["show_command"] = showCommands;
    } else {
      resultList[deviceIP]["config_command"] = "NA";
    }
  });
}

setVariable("expectedConfigData", resultList);

console.log("Devices to configure: ", devicesToConfigure);
console.log("All devices: ", allDevices);
console.log("Report available: ", reportAvailable);
console.log("Result list: ", resultList);
console.log("Error list: ", errorList);
console.log("Move Further: ", Object.keys(devicesToConfigure).length > 0);

