data.script:
const config_command = getVariable("configCommands");
const show_commands = getVariable("showCommands");
const snmp = getVariable("snmp");
const job_report_json_data = getVariable("rawShowOutput");

// Replace vars with actual values
const configCommandOutput = config_command.replace(/{{(.*?)}}/g, (_, varName) => getVariable(varName));

console.log("Config Command: ", configCommandOutput);
console.log("Show Commands: ", show_commands);
console.log("Job Report JSON Data: ", job_report_json_data);

let devicesToConfigure = {};
let allDevices = {};
let reportAvailable = true;
let resultList = {};
let errorList = {};
errorList.errors = [];

if (!job_report_json_data || !Array.isArray(job_report_json_data)) {
  reportAvailable = false;
  errorList.errors = ['No report available'];
  console.error("Invalid JSON data format.");
} else {
  job_report_json_data.forEach(dataItem => {
    if (!dataItem) {
      reportAvailable = false;
      errorList.errors = ['No data available'];
      return;
    }

    const device_ip = dataItem.ip_address;
    const reachability_status = dataItem.device_status === "Reachable";
    let change_required = true;
    let current_config = "";
    let authentication = false;

    if (dataItem.device_status === "Reachable") {
      authentication = true;
    }

    allDevices[device_ip] = true;

    if (!dataItem.data_dump_show_commands || !Array.isArray(dataItem.data_dump_show_commands) || dataItem.data_dump_show_commands.length < 1) {
      change_required = null;
      current_config = "NA";

      resultList[device_ip] = {
        reachability_status,
        authentication,
        change_required,
        current_config
      };

      reportAvailable = false;
      errorList.errors = [`No data available for ${device_ip}`];
      return;
    }

    let data_found = false;
    let jobOutputNotAvailable = false;

    dataItem.data_dump_show_commands.forEach(command => {
      if (command && command.json_output && Array.isArray(command.json_output) && command.command_name === `${show_commands}`) {
        const array = command.json_output;

        for (let i = 0; i < array.length; i++) {
          if (typeof array[i] === "string" && array[i].toLowerCase().includes(snmp.toLowerCase())) {
            change_required = true;
            data_found = true;
            break;
          }
        }
        if (!data_found) {
          change_required = false;
          data_found = true;
        }
        current_config = command.command_output;
      } else {
        jobOutputNotAvailable = true;
        change_required = null;
        current_config = "No output available";
        errorList.errors.push(`No output available for ${device_ip}`);
      }
    });

    resultList[device_ip] = {
      reachability_status,
      authentication,
      change_required,
      current_config
    };

    if (!data_found && !jobOutputNotAvailable) {
      devicesToConfigure[device_ip] = true;
      resultList[device_ip]["config_command"] = config_command;
    } else {
      resultList[device_ip]["config_command"] = 'NA';
    }
  });
}

function formatResultListForEmail(resultList) {
  let html = `
    <h2>Expected Device Configuration Report</h2>
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; font-family: Arial, sans-serif;">
      <thead>
        <tr style="background-color: #f2f2f2;">
          <th>Device IP</th>
          <th>Reachability</th>
          <th>Authenticated</th>
          <th>Change Required</th>
          <th>Show Command</th>
          <th>Current Config</th>
          <th>Config Command</th>
        </tr>
      </thead>
      <tbody>
  `;

  for (const [ip, data] of Object.entries(resultList)) {
    html += `
      <tr>
        <td>${ip}</td>
        <td>${data.reachability_status ? "Yes" : "No"}</td>
        <td>${data.authentication ? "Yes" : "No"}</td>
        <td>${data.change_required === null ? "Unknown" : data.change_required ? "Yes" : "No"}</td>
        <td><pre>${data.show_command || "NA"}</pre></td>
        <td><pre style="white-space: pre-wrap; max-width: 600px;">${data.current_config}</pre></td>
        <td><pre>${data.config_command || "NA"}</pre></td>
      </tr>
    `;
  }

  html += `
      </tbody>
    </table>
  `;

  return html;
}

const emailBody = formatResultListForEmail(resultList)
setVariable("plainTextOutput", emailBody)

// console.log("viewpoint_output", S(JSON.stringify(resultList)))
setVariable("expectedConfigData", resultList);
console.log("devices_to_configure", JSON.stringify(devicesToConfigure, null, 2));
console.log("error_list", JSON.stringify(errorList, null, 2));
console.log("report_available", reportAvailable);

const allConfigsAlreadyRemoved = Object.values(resultList).every(device => {
  const currentConfig = device.current_config?.toLowerCase() || "";
  return (
    currentConfig.includes("no output received") ||
    !currentConfig.includes(snmp.toLowerCase())
  );
});

setVariable("allNoOutput", allConfigsAlreadyRemoved);
//
console.log("All devices already removed or no output:", allConfigsAlreadyRemoved);

if (allConfigsAlreadyRemoved) {
  //
  console.log("Halting flow: nothing to remove.");
  return false;
}

//
console.log("Proceeding: at least one device has config to remove.");

return Object.keys(devicesToConfigure).length > 0;

/*
Available Context:
- handleError(error): Handle an error by displaying a snackbar message.
- setVariable(name, value): Set a variable with the given name and value.
- getVariable(name): Get the value of a variable with the given name.
- clearVariables(): Clear all variables.
- result: This variable contains the returned value from previous node.

Example:
  setVariable('exampleVar', 'exampleValue');
  const value = getVariable('exampleVar');
  if (value) {
    console.log(value)
  } else {
    handleError('Variable not found');
  }
*/

// Run your code here
const allNoOutput = getVariable("allNoOutput")

if (allNoOutput) {
  handleError("No Data Found")
} else {
  handleError("Configuration already exists")
}
